mapper.select:
select
,
, carpool_route_name from carpool_order left join carpool_match on(carpool_order.match_id =carpool_match.match_id) left join carpool_route on(carpool_match.carpool_route_id = carpool_route.carpool_route_id) where carpool_order.order_no = #{orderNo,jdbcType=CHAR}

SELECT * FROM carpool_order 
where 1 =1

	and match_id = #{matchId,jdbcType=VARCHAR}


	and city_code=#{cityCode,jdbcType=VARCHAR}

order by id ASC
